import { createContext, useContext, useState } from "react";
import { executeBasicAuthenticationsService } from "../components/api/HelloWorldApiService";
import { apiClinet } from "../components/api/ApiClient";

//1: Create a Context
export const AuthContext = createContext()

export const useAuth = () => useContext(AuthContext)

//2: Share the created context with other components
export default function AuthProvider({ children }) {

    //3: Put some state in the context
    const [isAuthenticated, setAuthenticated] = useState(false)
    const [username, setUsername] = useState(null)
    const [token, setToken] = useState(null)

   async function login(username, password){
    const baToken = 'Basic ' + window.btoa(username + ":" + password) // base64 인코딩
    try{
        const response = await executeBasicAuthenticationsService(baToken)
        if(response.status === 200){
            setAuthenticated(true)
            setUsername(username)
            setToken(baToken);

            apiClinet.interceptors.request.use(
                (config) => {
                    console.log('intercepting and adding a token')
                    config.headers.Authorization=baToken
                    return config
                }
            )
            return true
        }
        else{
            logout()
            return false
        }
    }
    catch(error){
        logout()
        return false
    }

   }

    function logout() {
        setAuthenticated(false)
        setToken(null)
        setUsername(null)
    }

    return (
        <AuthContext.Provider value={ {isAuthenticated, login, logout, username, token}  }>
            {children}
        </AuthContext.Provider>
    )
}
